LBaseElement* bGeometryElement::create(bGenericXMLBaseElement* elt){	return(new bGeometryElement(elt,(bGenericMacMapApp*)elt->getapp(),elt->getbundle()));}// ---------------------------------------------------------------------------// Destructeur// ------------bGeometryElement::~bGeometryElement(){}// ---------------------------------------------------------------------------// Initialisation// -----------void bGeometryElement::init(void* ctx){	((bGenericStyle*)ctx)->setrungeometry(this);	bStdXMLRenderingElement::init(ctx);}